# Global non-gap sequence alignment function
def global_alignment_score(seq1, seq2):
    # Fill the sequence to match the length, which is simplified here by filling with 'X'
    len_seq1, len_seq2 = len(seq1), len(seq2)
    max_len = max(len_seq1, len_seq2)
    seq1 += 'X' * (max_len - len_seq1)
    seq2 += 'X' * (max_len - len_seq2)
    
    score = 0
    for i in range(max_len):
        if seq1[i] == seq2[i]:
            score += BLOSUM62.get(seq1[i] + seq2[i], -1)
        else:
            score += BLOSUM62.get(seq1[i] + seq2[i], -1)
    
    return score

# Calculate the percentage similarity of the sequence
def calculate_identity(seq1, seq2):
    len_seq1, len_seq2 = len(seq1), len(seq2)
    max_len = min(len_seq1, len_seq2)
    identical = sum(s1 == s2 for s1, s2 in zip(seq1[:max_len], seq2[:max_len]))
    return (identical / max_len) * 100

